INSERT IGNORE INTO products (id, name, description, price, original_price, image, category, rating, reviews, badge, in_stock) VALUES
(1, 'Ethereal Silk Maxi Dress', 'Flowing silk maxi dress with an ethereal drape and delicate pleating. Features adjustable spaghetti straps and a flattering empire waistline. Perfect for evening events or summer weddings.', 289, 389, 'https://images.unsplash.com/photo-1595777457583-95e059d581b8?w=600&q=80', 'Dresses', 4.8, 234, 'Best Seller', true),
(2, 'Urban Leather Moto Jacket', 'Premium genuine leather motorcycle jacket with asymmetric zip closure. Features quilted shoulder panels, multiple pockets, and adjustable belt. A timeless piece for any wardrobe.', 459, 599, 'https://images.unsplash.com/photo-1551028719-00167b16eac5?w=600&q=80', 'Jackets', 4.9, 187, 'Premium', true),
(3, 'Cloud Runner Pro Sneakers', 'Ultra-lightweight running sneakers with responsive CloudTech cushioning. Breathable mesh upper, reinforced heel counter, and high-grip rubber outsole for all-terrain performance.', 189, 249, 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=600&q=80', 'Sneakers', 4.7, 456, 'New', true),
(4, 'Artisan Leather Tote Bag', 'Handcrafted Italian leather tote with gold-tone hardware. Spacious interior with laptop sleeve, zip pocket, and magnetic snap closure. Includes detachable shoulder strap.', 345, 450, 'https://images.unsplash.com/photo-1548036328-c9fa89d128fa?w=600&q=80', 'Bags', 4.6, 128, 'Handcrafted', true),
(5, 'Chronograph Titanium Watch', 'Swiss-made automatic chronograph with titanium case and sapphire crystal. Features date display, luminous hands, and water resistance to 200m. 42mm case diameter.', 875, 1199, 'https://images.unsplash.com/photo-1524592094714-0f0654e20314?w=600&q=80', 'Watches', 4.9, 89, 'Luxury', true),
(6, 'Aviator Polarized Sunglasses', 'Classic aviator sunglasses with polarized lenses and titanium frame. UV400 protection, anti-glare coating, and spring hinges for a comfortable fit. Includes premium leather case.', 195, 250, 'https://images.unsplash.com/photo-1572635196237-14b3f281503f?w=600&q=80', 'Sunglasses', 4.5, 312, NULL, true),
(7, 'Cashmere Blend Overcoat', 'Luxurious cashmere-wool blend overcoat with notch lapels, single-breasted closure, and side pockets. Fully lined in silk for ultimate comfort. A sophisticated winter essential.', 599, 799, 'https://images.unsplash.com/photo-1539533018447-63fcce2678e3?w=600&q=80', 'Jackets', 4.8, 76, 'Premium', true),
(8, 'Minimalist Canvas Backpack', 'Water-resistant waxed canvas backpack with genuine leather accents. Roll-top closure, padded laptop compartment, and hidden zipper pocket. Perfect for urban commuters.', 129, 169, 'https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=600&q=80', 'Bags', 4.4, 203, NULL, true),
(9, 'Velvet Evening Gown', 'Stunning velvet evening gown with a sweetheart neckline and dramatic thigh-high slit. Features built-in corset boning and invisible zipper closure. Available in rich jewel tones.', 425, 575, 'https://images.unsplash.com/photo-1566174053879-31528523f8ae?w=600&q=80', 'Dresses', 4.7, 145, 'Trending', true),
(10, 'Retro High-Top Sneakers', 'Vintage-inspired high-top sneakers with premium suede and canvas construction. Features cushioned insole, reinforced toe cap, and classic vulcanized rubber sole.', 145, 185, 'https://images.unsplash.com/photo-1606107557195-0e29a4b5b4aa?w=600&q=80', 'Sneakers', 4.6, 378, NULL, true),
(11, 'Smart Fitness Watch', 'Advanced fitness smartwatch with AMOLED display, heart rate monitoring, GPS tracking, and 7-day battery life. Water-resistant to 50m with interchangeable bands.', 329, 429, 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=600&q=80', 'Watches', 4.5, 567, 'Tech', true),
(12, 'Bohemian Wrap Dress', 'Free-spirited wrap dress with a vibrant floral print. Made from lightweight viscose fabric with flutter sleeves and an adjustable tie waist. Effortlessly chic for any occasion.', 159, 210, 'https://images.unsplash.com/photo-1515372039744-b8f02a3ae446?w=600&q=80', 'Dresses', 4.3, 201, NULL, true),
(13, 'Oversized Denim Jacket', 'Relaxed-fit denim jacket in premium washed cotton. Features button front closure, chest pockets, and adjustable side tabs. Versatile layering piece for all seasons.', 178, 230, 'https://images.unsplash.com/photo-1543076447-215ad9ba6923?w=600&q=80', 'Jackets', 4.4, 289, NULL, true),
(14, 'Quilted Crossbody Bag', 'Elegant quilted leather crossbody bag with gold chain strap. Features front flap with turn-lock closure, interior card slots, and microfiber lining. Compact yet spacious.', 265, 340, 'https://images.unsplash.com/photo-1584917865442-de89df76afd3?w=600&q=80', 'Bags', 4.7, 156, 'Trending', true),
(15, 'Sport Wayfarer Sunglasses', 'Modern wayfarer sunglasses with gradient lenses and lightweight acetate frame. UV400 protection with scratch-resistant coating. Comfortable rubber nose pads.', 135, NULL, 'https://images.unsplash.com/photo-1511499767150-a48a237f0083?w=600&q=80', 'Sunglasses', 4.3, 245, NULL, true),
(16, 'Chunky Platform Sneakers', 'Bold chunky platform sneakers with mixed-material upper. Features thick EVA midsole for extra height, breathable mesh panels, and retro color blocking.', 165, 220, 'https://images.unsplash.com/photo-1600185365926-3a2ce3cdb9eb?w=600&q=80', 'Sneakers', 4.5, 198, 'New', true),
(17, 'Classic Dive Watch', 'Professional dive watch with unidirectional rotating bezel and luminous markers. 300m water resistance, screw-down crown, and automatic movement with 48-hour power reserve.', 695, 950, 'https://images.unsplash.com/photo-1522312346375-d1a52e2b99b3?w=600&q=80', 'Watches', 4.8, 112, 'Luxury', true),
(18, 'Satin Slip Midi Dress', 'Elegant satin slip dress with cowl neckline and bias cut for a flattering silhouette. Adjustable straps and side slit detail. Pairs beautifully with heels or sneakers.', 198, NULL, 'https://images.unsplash.com/photo-1496747611176-843222e1e57c?w=600&q=80', 'Dresses', 4.6, 167, NULL, true),
(19, 'Puffer Down Jacket', 'Warm puffer jacket with 800-fill responsibly sourced goose down. Features water-resistant shell, packable design, and stand collar with hidden hood. Lightweight yet incredibly warm.', 349, 450, 'https://images.unsplash.com/photo-1544022613-e87ca75a784a?w=600&q=80', 'Jackets', 4.7, 234, 'Best Seller', true),
(20, 'Woven Straw Beach Bag', 'Hand-woven straw beach bag with leather handles and cotton lining. Spacious interior fits all your beach essentials. Features interior zip pocket and magnetic snap closure.', 89, 120, 'https://images.unsplash.com/photo-1590874103328-eac38a683ce7?w=600&q=80', 'Bags', 4.2, 98, NULL, true),
(21, 'Running Performance Shoes', 'Elite running shoes with carbon fiber plate and nitrogen-infused foam. Engineered mesh upper conforms to your foot for a locked-in feel. Built for speed and endurance.', 225, 290, 'https://images.unsplash.com/photo-1595950653106-6c9ebd614d3a?w=600&q=80', 'Sneakers', 4.8, 432, 'Performance', true),
(22, 'Cat-Eye Designer Sunglasses', 'Retro-inspired cat-eye sunglasses with gradient lenses. Handmade acetate frame with metal temple details and logo-embossed tips. A bold statement piece.', 225, 295, 'https://images.unsplash.com/photo-1473496169904-658ba7c44d8a?w=600&q=80', 'Sunglasses', 4.6, 178, 'Designer', true),
(23, 'Tailored Wool Blazer', 'Impeccably tailored blazer in Italian wool blend. Features peak lapels, double-breasted closure, and working sleeve buttons. Finished with a slim silhouette and satin lining.', 385, 520, 'https://images.unsplash.com/photo-1507679799987-c73779587ccf?w=600&q=80', 'Jackets', 4.9, 64, 'Premium', true),
(24, 'Floral Midi Skirt', 'Romantic floral midi skirt in lightweight georgette fabric. Features elastic waistband, asymmetric hemline, and vibrant botanical print. Pairs perfectly with simple tops.', 112, 150, 'https://images.unsplash.com/photo-1583496661160-fb5886a0aaaa?w=600&q=80', 'Dresses', 4.4, 143, NULL, true),
(25, 'Leather Weekend Duffle', 'Full-grain leather weekend duffle bag with brass hardware. Features U-shaped top opening, interior shoe compartment, and removable shoulder strap. Ages beautifully over time.', 415, 550, 'https://images.unsplash.com/photo-1622560480605-d83c853bc5c3?w=600&q=80', 'Bags', 4.8, 87, 'Handcrafted', true);
